<?phpnamespace Clases;class Request{    public function get($key, $default = null)    {        return $_GET[$key] ?? $default;    }    public function post($key, $default = null)    {        return $_POST[$key] ?? $default;    }    public function session($key, $value = null)    {        if ($value === null) {            return $_SESSION[$key] ?? null;        } else {            $_SESSION[$key] = $value;        }    }        public function startSession()    {        if (session_status() == PHP_SESSION_NONE) {            session_start();        }    }}