create table usuarios(    id int(11) primary key auto_increment,    nombre varchar(100) not null,    apellido varchar(100) not null,    email varchar(100) not null,    telefono varchar(8) not null,    password varchar(100) not null,    admin tinyint(1) default 0,    confirmado tinyint(1) default 1,    token varchar(15),    created_at timestamp default current_timestamp);